mapper.select:
select

from dm_cust_order



            and merchant_id = #{merchantId}


            and order_no = #{orderNo}



              and create_time >= DATE_SUB(DATE_FORMAT(now(),'%y-%m-%d'),INTERVAL 1 MONTH)




              and create_time >= DATE_SUB(DATE_FORMAT(now(),'%y-%m-%d'),INTERVAL 6 MONTH)




                and state in ('10A','10B')


                and state = #{state}




order by create_time desc

select

from dm_cust_order
where merchant_id = #{merchantId}
and ORDER_ID = #{orderId,jdbcType=INTEGER}
